changeSet.sql:
create table users (id integer generated by default as identity, username varchar(255), created_at date,
primary key (id));

create table posts (id integer generated by default as identity, url varchar(255), created_at date, user_id integer,
primary key (id));

create table comments (id integer generated by default as identity, content varchar(255), created_at date,
edited_at date, user_id integer, post_id integer, primary key (id));

alter table posts add constraint FK_USER_POST foreign key (user_id) references users;
alter table comments add constraint FK_USER_COMMENT foreign key (user_id) references users;
alter table comments add constraint FK_POST_COMMENT foreign key (post_id) references posts;

create view user_simple_view as (select u.id, u.username, u.created_at, count(p.id) as amount_of_posts,
count(c.id) as amount_of_comments from users u left outer join posts p on (u.id = p.user_id)
left outer join comments c on (u.id = c.user_id) group by u.id);

create view post_simple_view as (select p.id, p.url, p.created_at, p.user_id, count(c.id)
as amount_of_comments from posts p left outer join comments c on (p.id = c.post_id) group by p.id);
